import React, {Component} from 'react';
import styled from 'styled-components';


const Container = styled.div`
  min-width: 100vw;
  min-height:100vh;
  background: ${props=> props.color};
  position:relative;

`

const LeftContainer = styled.div`
  min-width: 30vw;
  max-height: 100vh;
  display: flex;
  flex-direction: column;
  float:left;
  @media (max-width: 480px) {
    max-height: unset;
  }
  @media (min-width: 768px) and (max-width: 1024px) and (orientation:portrait) {
    min-width: 50vw;
  }
`
const Topbox = styled.div`
  min-width: 30vw;
  min-height: 50vh;
  width:30vw;
  height:50vh;
  padding:0px 0px 0px 0px;
`
const Bottombox= styled.div`
  min-width: 30vw;
  min-height: 50vh;
  width:30vw;
  height:50vh;
  padding:0px 0px 0px 0px;
`

const RightContainer = styled.div`
  min-width: 70vw;
  height: 100vh;
  float:right;
  position:relative;
  @media (max-width: 480px) {
    height:50vh;
  }
  @media (min-width: 768px) and (max-width: 1024px) and (orientation:portrait) {
    min-width: 50vw;
  }

`

const Img = styled.img`
  width:70vw;
  height:100%;
  border:none;
  @media (max-width: 480px) {
    width:100vw;
    height:50vh;
  }
  @media (min-width: 768px) and (max-width: 1024px) and (orientation:portrait) {
    width: 50vw;
  }
`
const Imgtop = styled.img`
  width:100%;
  height:100%;
  border:none;
  @media (max-width: 480px) {
    width:100vw;
    height:50vh;
  }
  @media (min-width: 768px) and (max-width: 1024px) and (orientation:portrait) {
    width: 50vw;
  }
`

const FloatingText = styled.div`
position:absolute;
left:-10%;
top:10%;
color:white;
z-index:20;
width:32%;
background:#1f1f1f;
@media (max-width: 480px){
position:unset;
text-align:center;
display:none;
}
`

const Text1 = styled.h1`
font-size:120px;
margin:0px;
text-align:left;
padding:10px
`
const Text2 = styled.p`
margin:0px;
text-align:left;
background=color:black;
padding:20px
`
const Text3 = styled.p`
margin:0px;
text-align:left;
background=color:black;
padding:20px
`
class Parallax2 extends Component {

render() {

    return (

      <Container color={this.props.color}>

        <LeftContainer>

          <Topbox >
          <Imgtop src={this.props.img1} frameborder="0"></Imgtop>
          </Topbox>

          <Bottombox >
          <Imgtop src={this.props.img1} frameborder="0"></Imgtop>
          </Bottombox>

        </LeftContainer>

        <RightContainer >
          <Img src={this.props.img2} alt=""/>
          <Text1 >{this.props.text1}</Text1>

        <FloatingText>

          <Text2 >{this.props.text2}</Text2>
          <Text3 >{this.props.text3}</Text3>
        </FloatingText>

        </RightContainer>

      </Container>
  );
  }
}

export default Parallax2;
