import React, {Component} from 'react';
import styled from 'styled-components';
import Port from '../images/me2.png'
import AOS from 'aos';
import 'aos/dist/aos.css';
import Typical from 'react-typical'

const Container = styled.div`

height:100%;
display:flex;
background:#020202;
position:relative;

@media (min-width: 1px) and (max-width: 426px) {
  flex-direction:column


 }
`

const Holder = styled.div`
width: 33vw;
@media (min-width: 1px) and (max-width: 426px) {
  width: 60vw; 
   }
`

const HeadingAnimated =styled.h1`
color:white;
font-family:arial;
margin:0px;
font-size:2vw;
font-weight:bolder;
margin:10% 0% 5% 0%;
@media (min-width: 1px) and (max-width: 426px) {
font-size:4vw; 
 }
`

const Heading =styled.h1`
color:white;
font-family:arial;
margin:0px;
font-size:2vw;
font-weight:bolder;
@media (min-width: 1px) and (max-width: 426px) {
  font-size:5vw; 
   }
`
const HeadingBottom =styled.h1`
color:white;
font-family:arial;
margin:0px;
font-size:1.5vw;
font-weight:bolder;

@media (min-width: 1px) and (max-width: 426px) {

padding-left:5vw;
padding-top:5vw;
font-size:3vw; 
 }
`
const Heading2 =styled.h1`
font-size:6vw;
color:white;
font-family:arial;
margin:4% 0%;
font-weight:bolder;
@media (min-width: 1px) and (max-width: 426px) {
  font-size:9vw; 
   }
`

const Heading3 =styled.h1`
font-size:2vw;
color:white;
font-family:arial;
margin:10% 0% 0% 0%;
font-weight:bolder;
@media (min-width: 1px) and (max-width: 426px) {
  font-size:7vw; 
   }
`

const Para =styled.p`
font-size:1vw;
color:white;
letter-spacing:2px;
line-height:1.7vw;
font-family:arial;
margin:2vw 0% 0% 0%;
font-weight:bolder;
@media (min-width: 1px) and (max-width: 426px) {
  font-size:2vw;
  line-height:1vw;

   }
`
const Focus =styled.h1`
font-size:7vw;
color:white;
margin:0px;
font-family:arial;
@media (min-width: 1px) and (max-width: 426px) {
  font-size:10vw; 
   }
`

const LeftContainer = styled.div`
height:100%;
background:#020202;
padding-left:5vw;
`

const RightContainer = styled.div`

display:flex;
background:#020202;
position:absolute;
right:0px
@media (min-width: 1px) and (max-width: 426px) {
  margin:0vw;
  position:relative;
 }
`

const RightContainerImg = styled.img`
height:40vw
width:40vw
    margin-top: 20vw;
@media (min-width: 1px) and (max-width: 426px) {
  width:100%
  margin-top: 7vw;
  height:100vw
 }
`

class Page3 extends Component {
  componentDidMount(){
    AOS.init({
      duration : 1000
    })
  }
render() {

    return (

      <Container id="Home">

     <LeftContainer>
     <HeadingAnimated data-aos='slide-up'>
        <Typical
              steps={['Hello!', 1000,'Hola!',1000,'Bonjour!',1000,'Konnichiwa!',1000,'God dag!',1000,'Shalom!',1000]}
              loop={Infinity}
              wrapper={"h1"}
            />
             <Heading data-aos='slide-up'> my name is Zaheer.</Heading>
        </HeadingAnimated>


     <Heading3 data-aos='slide-up'> I am a</Heading3>
        <Heading2 className="text-flicker-in-glow" >FRONT-END<br/><Focus>DEVELOPER</Focus></Heading2>

        <Holder>
          <Para>Lorem ipsum dolor sit amet consectetur adipisicing elit. Natus molestias nihil nam deleniti quasi odio saepe, illo repellat obcaecati, id porro esse impedit ut labore pariatur nobis neque accusantium veniam rem est suscipit a debitis. Ex consequatur, cupiditate aperiam iusto hic sequi deleniti qui quas totam rem sed placeat fugiat.</Para>
        </Holder>
     </LeftContainer>



      <RightContainer data-aos='slide-left'>

      <RightContainerImg src={Port} alt=""/>

      </RightContainer>
      </Container>
  );
  }
}

export default Page3;
