  position:relative;

  display:flex;
  @media (max-width: 480px) {
    flex-direction: column;

  }
`

const LeftContainer = styled.div`
  width: 50%;
  max-height: 100vh;
  display: flex;
  flex-direction: column;
  background:#1f1f1f;
  float:left;
  @media (max-width: 480px) {
    width: 100%;
  }
  @media (min-width: 768px) and (max-width: 1024px) and (orientation:portrait) {
    min-width: 50vw;
  }
`


const RightContainer = styled.div`
  width: 50%;
  height: 100vh;
  float:right;
  position:relative;
  background-image:url(${zafs});
  background-size:100% 100%;
  @media (max-width: 480px) {

    width: 100%;
  }
  @media (min-width: 768px) and (max-width: 1024px) and (orientation:portrait) {
    min-width: 50vw;
  }

`



const FloatingText = styled.div`
position:relative;

