import React, {Component} from 'react';
import styled from 'styled-components';
import zafs from '../images/zafs.jpg'
import yellow1 from '../images/yellow1.jpg'
import yellow2 from '../images/yellow2.jpg'
import yellow3 from '../images/yellow3.jpg'

const Container = styled.div`
min-width: 100vw;
height: 100%;
display:flex;
overflow:hidden
background:#1f1f1f;
@media (min-width: 1px) and (max-width: 426px) {
 flex-direction:column
 margin: 0px;
}
`

const LeftContainer = styled.div`
width: 33.3%;
height: 100vh;
display:flex;
flex-direction:column

`
const LeftContainerTopBox = styled.div`
width:100%;
height: 50vh;

@media (min-width: 1px) and (max-width: 426px) {
  width: 100vw;
}
`
const LeftContainerBottomBox = styled.div`
width:100%;
height: 50vh;
overflow-y: auto;

@media  (min-width: 1px) and (max-width: 426px) {
  width: 100vw;
}
`

const MiddleContainer = styled.div`
width: 33.3%;
height: 100vh;
display:flex;
flex-direction:column

`
const MiddleContainerTopBox = styled.div`
width:100%;
height: 40vh;

@media (min-width: 1px) and (max-width: 426px) {
  width: 100vw;
}
`
const MiddleContainerBottomBox = styled.div`
width:100%;
height: 40vh;
overflow-y: auto;
@media  (min-width: 1px) and (max-width: 426px) {
  width: 100vw;
}
`

const RightContainer = styled.div`
  width: 33.3%;
  height: 100vh;
  float:right;
  position:relative;
  margin: 20px;
    margin-top: 0px;
    justify-content: space-evenly;
@media  (min-width: 1px) and (max-width: 426px) {
  width: 100vw;
}

`
const RightContainerTopBox = styled.div`
width:100%;
height: 40vh;
@media (min-width: 1px) and (max-width: 426px) {
  width: 100vw;
}
`

const RightContainerBottomBox = styled.div`
width:100%;
height: 40vh;
overflow-y: auto;
@media  (min-width: 1px) and (max-width: 426px) {
  width: 100vw;
}
`
const Figure = styled.figure`
width: 100%;
    height: 100%;
    position: relative;
    margin: 0px 0px;
`
const FigureCaption = styled.figcaption`
background-color: #1f1f1f;
color: #fff;
font: italic smaller sans-serif;
padding: 3px;
text-align: center;
position: absolute;
bottom: 0px;
width: auto;
padding: 20px;
`
const FigureImg = styled.img`
width: 100%;
height: 100%;
`
class Page2 extends Component {

render() {

    return (

      <Container color={this.props.color}>


        <LeftContainer>

          <LeftContainerTopBox>
          <Figure>
            <FigureImg src={yellow1}
            alt="Elephant at sunset"/>
          <FigureCaption>An elephant at sunset</FigureCaption>
          </Figure>
          </LeftContainerTopBox>

          <LeftContainerBottomBox>
          <Figure>
            <FigureImg src={yellow1}
            alt="Elephant at sunset"/>
          <FigureCaption>An elephant at sunset</FigureCaption>
          </Figure>
          </LeftContainerBottomBox>

        </LeftContainer>

        <MiddleContainer>

        <MiddleContainerTopBox>
        <Figure>
            <FigureImg src={yellow1}
            alt="Elephant at sunset"/>
          <FigureCaption>An elephant at sunset</FigureCaption>
          </Figure>
        </MiddleContainerTopBox>

        <MiddleContainerBottomBox>
        <Figure>
            <FigureImg src={yellow1}
            alt="Elephant at sunset"/>
          <FigureCaption>An elephant at sunset</FigureCaption>
          </Figure>
        </MiddleContainerBottomBox>

        </MiddleContainer>

        <RightContainer>

          <RightContainerTopBox>
          <Figure>
            <FigureImg src={yellow1}
            alt="Elephant at sunset"/>
          <FigureCaption>An elephant at sunset</FigureCaption>
          </Figure>
          </RightContainerTopBox>

          <RightContainerBottomBox>
          <Figure>
            <FigureImg src={yellow1}
            alt="Elephant at sunset"/>
          <FigureCaption>An elephant at sunset</FigureCaption>
          </Figure>
          </RightContainerBottomBox>


        </RightContainer>

      </Container>
  );
  }
}

export default Page2;
