import React, {Component} from 'react';
import styled from 'styled-components';
import Gallery from './Gallery.jsx'
import Crud from '../images/Crud.png'
import Kkwe from '../images/Kkwe.png'
import Port from '../images/Port.png'
import FunFox from '../images/FunFox.png'
import AOS from 'aos';
import 'aos/dist/aos.css';

const Container = styled.div`
height:100%;

margin: 5vw 0% 0% 0%;
@media (min-width: 1px) and (max-width: 426px) {
 flex-direction:column
 margin: 0px;
 height: unset;
}
 @media (min-width: 427px) and (max-width: 768px) {

  }
 @media (min-width: 769px) and (max-width: 1024px) {

  }
`

const  Holder = styled.div`


display:flex;
margin: 20px 0px;
margin-bottom: 0px;
padding-bottom: 0px;

flex-direction:row;
@media (min-width: 1px) and (max-width: 426px) {
 width:100%;
 }
`


const Figure = styled.figure`
width: 100%;
    height: 100%;
    position: relative;
    margin: 10px 10px;


`


const FigureCaption = styled.figcaption`
background-color: #1f1f1f;
color: #fff;
font: italic smaller sans-serif;
padding: 3px;
text-align: center;
position: absolute;
bottom: 0px;
width: auto;
padding: 20px;
`
const FigureImg = styled.img`
width: 100%;
height: 100%;

`
const Heading2 =styled.h1`
font-size:6vw;
color:white;
font-family:arial;
margin:4% 0%;
font-weight:bolder;
text-align:right;
`

const TextHolder =styled.div`
height:50vh;
`
class Page2 extends Component {
  componentDidMount(){
    AOS.init({
      duration : 1000
    })
  }
render() {

    return (

      <Container className="dragscroll" id="gallery">
        <TextHolder>
  <Heading2 className="text-flicker-in-glow" data-aos='slide-up' >PROJECTS</Heading2>
  </TextHolder>
  <Holder> 
          <Figure>

          <FigureImg src={Crud}
            alt="Elephant at sunset"/>
          <a href="https://zeus540.github.io/react-firebase-crud-app/" target="_blank" rel="noopener noreferrer"><FigureCaption>Firebase Crud App</FigureCaption></a>
          </Figure>



          <Figure>
          <FigureImg src={Port}
            alt="Elephant at sunset"/>
          <a href="https://zaheer.netlify.app" target="_blank" rel="noopener noreferrer"><FigureCaption>SPA Portfolio</FigureCaption></a>
          </Figure>



          <Figure>
          <FigureImg src={Kkwe}
            alt="Elephant at sunset"/>
          <a href="https://kkwe.co.za" target="_blank" rel="noopener noreferrer"><FigureCaption>Kkwe</FigureCaption></a>
          </Figure>




          <Figure>
          <FigureImg src={FunFox}
            alt="Elephant at sunset"/>
          <FigureCaption>Fun Fox</FigureCaption>
          </Figure>


          </Holder>

      </Container>
  );
  }
}

export default Page2;
