import React, {Component} from 'react';
import styled from 'styled-components';
import yellow1 from '../images/yellow1.jpg'

const Container = styled.div`
  min-width: 100vw;
  min-height:100vh;
  position:relative;
  display:flex;
  @media (max-width: 480px) {
    flex-direction: column;

  }
`

const LeftContainer = styled.div`
  min-width: 30vw;
  max-height: 100vh;
  display: flex;
  flex-direction: column;
  float:left;
  @media (max-width: 480px) {
    max-height: unset;
  }
  @media (min-width: 768px) and (max-width: 1024px) and (orientation:portrait) {
    min-width: 50vw;
  }
`
const Topbox = styled.div`
  min-width: 30vw;
  min-height: 50vh;
  width:30vw;
  height:50vh;
  padding:0px 0px 0px 0px;
`
const Bottombox= styled.div`
  min-width: 30vw;
  min-height: 50vh;
  width:30vw;
  height:50vh;
  padding:0px 0px 0px 0px;
`

const RightContainer = styled.div`
  min-width: 70vw;
  height: 100vh;
  float:right;
  position:relative;
  background-image:url(${yellow1});
  background-size:100% 100%;
  @media (max-width: 480px) {
    height:50vh;
  }
  @media (min-width: 768px) and (max-width: 1024px) and (orientation:portrait) {
    min-width: 50vw;
  }

`

const Imgtop = styled.img`
  width:100%;
  height:100%;
  border:none;
  @media (max-width: 480px) {
    width:100vw;
    height:50vh;
  }
  @media (min-width: 768px) and (max-width: 1024px) and (orientation:portrait) {
    width: 50vw;
  }
`

const FloatingText = styled.div`
position:relative;
color:white;
z-index:20;
overflow-y:auto;
margin:4% 5% 10px 5%;
@media (max-width: 768px){
text-align:center;

}
`
const Text2 = styled.p`
margin:0px;
text-align:left;
background=color:black;
overflow-y:auto;
width:45%
color:white;
float:left;

padding:15px
@media (max-width: 768px) {
  width: auto;

}
`

const Text1 = styled.h1`
font-size:120px;
margin:0px;
text-align:left;
margin:10% 0px 0px 5%;
color:white;
position:relative;
z-index:20;
padding:0px;
@media (max-width: 480px) {
  font-size:70px;
}
`


const FloatingText2 = styled.div`
margin:0px;
text-align:left;
background=color:black;
overflow-y:auto;
margin:2% 5% 0px 5%;
`
const Text3 = styled.p`
margin:0px;
text-align:left;
background=color:black;
overflow-y:auto;
width:45%
color:white;
float:right;

padding:15px
@media (max-width: 768px) {
  width: auto;
}
`

class Parallax2 extends Component {

render() {

    return (

      <Container color={this.props.color}>

        <LeftContainer>

          <Topbox >
          <Imgtop src={this.props.img1} frameborder="0"></Imgtop>
          </Topbox>

          <Bottombox >
          <Imgtop src={this.props.img1} frameborder="0"></Imgtop>
          </Bottombox>

        </LeftContainer>

        <RightContainer >

        <Text1>Heading</Text1>
        <FloatingText>

          <Text2>The AF1 React D/MS/X optimizes comfort with a full length Nike React midsole equipped with a heel Ramp Air unit. The upper features a large bold Swoosh and mesh construction on the side panels. The traditional AIR logo appears on the heel, along with a newly added Nike React branded stripe. Nike React’s pattern replaces the traditional stars at the heel and toe of the outsole.The AF1 React D/MS/X optimizes comfort with a full length Nike React midsole equipped with a heel Ramp Air unit. The upper features a large bold Swoosh and mesh construction on the side panels.</Text2>

        </FloatingText>

        <FloatingText2>

        <Text3>The AF1 React D/MS/X optimizes comfort with a full length Nike React midsole equipped with a heel Ramp Air unit. The upper features a large bold Swoosh and mesh construction on the side panels. The traditional AIR logo appears on the heel, along with a newly added Nike React branded stripe. Nike React’s pattern replaces the traditional stars at the heel and toe of the outsole.  The traditional AIR logo appears on the heel, along with a newly added Nike React branded stripe. Nike React’s pattern replaces the traditional stars at the heel and toe of the outsole.</Text3>

        </FloatingText2>
        </RightContainer>

      </Container>
  );
  }
}

export default Parallax2;
