import React, {Component} from 'react';
import styled from 'styled-components';
import Port from '../images/Port.png'
import Gallery from './Gallery.jsx'
import Back from '../images/back.jpg'
import AOS from 'aos';
import 'aos/dist/aos.css';
import Typical from 'react-typical'

const Container = styled.div`
padding-top:5%;
height:100%;
display:flex;
background:#020202;
`

const Heading =styled.h1`
color:white;
font-family:arial;
`

const Heading2 =styled.h1`
font-size:6vw;
color:white;
font-family:arial;
`
const Focus =styled.h1`
font-size:7vw;
color:white;
margin:0px;
font-family:arial;
`

const LeftContainer = styled.div`
height:100%;
background:#020202;
`

const RightContainer = styled.div`
height:100%;
display:flex;
background:#020202;
`

class Page3 extends Component {
  componentDidMount(){
    AOS.init({
      duration : 1000
    })
  }
render() {

    return (

      <Container id="Home">
     <LeftContainer>
     <Heading data-aos='fade-down'>Hello <br/>My Name is Zaheer.</Heading>
        <Heading2 data-aos='fade-down'>FRONT-END<br/><Focus>DEVELOPER</Focus></Heading2>
     </LeftContainer>

     <Typical
              steps={['REKT MEDIA', 1000,]}
              loop={Infinity}
              wrapper="h1"
            />

      <RightContainer>
      <img src="" alt=""/>
      </RightContainer>
      </Container>
  );
  }
}

export default Page3;
