width:100%;
height: 40vh;
overflow-y: auto;
@media  (min-width: 1px) and (max-width: 426px) {
  width: 100vw;
}
`
const Figure = styled.figure`
width: 100%;
    height: 100%;
    position: relative;
    margin: 0px 0px;
    overflow:hidden;

`
const FigureCaption = styled.figcaption`
background-color: #1f1f1f;
