import React, { Component,Fragment } from 'react'
import styled from 'styled-components'
import css from '../images/css.png'
import boot from '../images/boots.png'
import html from '../images/html.png'
import git from '../images/github.png'
import java from '../images/javascript.png'
import mail from '../images/mailchimp.png'
import npm from '../images/npm.png'
import word from '../images/wordpress.png'
import react from '../images/react.png'
import sass from '../images/sass.png'
import bit from '../images/bitbucket.png'
import AliceCarousel from 'react-alice-carousel'
import 'react-alice-carousel/lib/alice-carousel.css'
import Crud from '../images/Crud.png'

const Container = styled.div`
justify-content:center;
align-items:center;
margin:0 auto;
height:100%;
`


const LeftFigure = styled.figure`


    margin: 0px 0px;


`
const FigureImg = styled.img`
width: 100%;
height: 500px;

`
const FigureCaption = styled.figcaption`
background-color: #1f1f1f;
color: #fff;
font: italic smaller sans-serif;
padding: 3px;
text-align: center;
position: absolute;
bottom: 0px;
width: auto;
padding: 20px;
`
class Gallery extends Component {
  state = {
    galleryItems: [1, 2, 3].map((i) => <h2 key={i}>{i}</h2>),
  }

  responsive = {
    0: { items: 3 },
    375: { items: 3 },
    425: { items: 3 },
    768: { items: 4 },
    1024: { items: 5 },
    1440: { items: 6 },
  }

  onSlideChange(e) {
    console.debug('Item`s position during a change: ', e.item)
    console.debug('Slide`s position during a change: ', e.slide)
  }

  onSlideChanged(e) {
    console.debug('Item`s position after changes: ', e.item)
    console.debug('Slide`s position after changes: ', e.slide)
  }
  render() {



    return (

      <div className="row">
        <Container id="Hobbies">



                <AliceCarousel 

                items={this.state.galleryItems}
                responsive={this.responsive}
                autoPlayInterval={400}

                disableDotsControls={false}
                mouseTracking={true}
                autoPlayDirection="ltr"
                autoPlay={false}
                fadeOutAnimation={true}
                disableAutoPlayOnAction={false}
                disableButtonsControls={false}>

                  <div>
                  <LeftFigure>
                      <FigureImg src={Crud}
                        alt="Elephant at sunset"/>
                      <a href="https://zeus540.github.io/react-firebase-crud-app/" target="_blank" rel="noopener noreferrer"><FigureCaption>Firebase Crud App</FigureCaption></a>
                      </LeftFigure> 

                  </div>

                  <div>
                  <LeftFigure>
                      <FigureImg src={Crud}
                        alt="Elephant at sunset"/>
                      <a href="https://zeus540.github.io/react-firebase-crud-app/" target="_blank" rel="noopener noreferrer"><FigureCaption>Firebase Crud App</FigureCaption></a>
                      </LeftFigure> 

                  </div>

                  <div>
                  <LeftFigure>
                      <FigureImg src={Crud}
                        alt="Elephant at sunset"/>
                      <a href="https://zeus540.github.io/react-firebase-crud-app/" target="_blank" rel="noopener noreferrer"><FigureCaption>Firebase Crud App</FigureCaption></a>
                      </LeftFigure> 

                  </div>
                  <div>
                  <LeftFigure>
                      <FigureImg src={Crud}
                        alt="Elephant at sunset"/>
                      <a href="https://zeus540.github.io/react-firebase-crud-app/" target="_blank" rel="noopener noreferrer"><FigureCaption>Firebase Crud App</FigureCaption></a>
                      </LeftFigure> 

                  </div>

                </AliceCarousel>      



        </Container>
      </div>
    );
  }
}
export default Gallery;
