import React, {Component} from 'react';
import styled from 'styled-components';
import Back from '../images/back.jpg'
import Crud from '../images/Crud.png'
import Kkwe from '../images/Kkwe.png'
import Port from '../images/Port.png'
import FunFox from '../images/FunFox.png'

const Container = styled.div`

height:100vh;
display:flex;
background:#1f1f1f;

@media (min-width: 1px) and (max-width: 426px) {
 flex-direction:column
 margin: 0px;
 height: unset;
}
 @media (min-width: 427px) and (max-width: 768px) {

  }
 @media (min-width: 769px) and (max-width: 1024px) {

  }
`

const LeftContainer = styled.div`
width: 33.3%;

display:flex;
margin: 20px 0px;
margin-bottom: 0px;
padding-bottom: 0px;
overflow-y:scroll;
flex-direction:column
@media (min-width: 1px) and (max-width: 426px) {
 width:100%;
 }
`
const LeftContainerTopBox = styled.div`
width:90%;
height: 100%;
margin: 0 auto;
margin-bottom:5%;
@media (min-width: 1px) and (max-width: 426px) {
  width: 100vw;
}
`

const MiddleContainer = styled.div`
width: 33.3%;
display:flex;
flex-direction:column
padding: 20px;
background-size:100% 100%;
background-image: url(${Back});
padding-bottom: 0px;
place-content: space-evenly;
background-attachment: fixed;
@media (min-width: 1px) and (max-width: 426px) {
  width:100%;
  padding: 0px;
  }
`

const Heading = styled.h1`
color: white;
    text-align: center;
    letter-spacing: 7px;
    font-family:arial;
`

const RightContainer = styled.div`
width: 33.3%;
display:flex;
margin: 20px 0px;
margin-bottom: 0px;
padding-bottom: 0px;
overflow-y:auto;
flex-direction:column
@media  (min-width: 1px) and (max-width: 426px) {
  width: 100vw;

}

`
const RightContainerTopBox = styled.div`
width:90%;
height: 100%;
margin: 0 auto;
margin-bottom:5%;
@media (min-width: 1px) and (max-width: 426px) {
  width: 100vw;
}
`

const LeftFigure = styled.figure`
width: 100%;
    height: 100%;
    position: relative;
    margin: 0px 0px;
    overflow:hidden;

`

const RightFigure = styled.figure`
width: 100%;
    height: 100%;
    position: relative;
    margin: 0px 0px;
    overflow:hidden;


`
const FigureCaption = styled.figcaption`
background-color: #1f1f1f;
color: #fff;
font: italic smaller sans-serif;
padding: 3px;
text-align: center;
position: absolute;
bottom: 0px;
width: auto;
padding: 20px;
`
const FigureImg = styled.img`
width: 100%;
height: 100%;

`

class Page2 extends Component {

render() {

    return (

      <Container className="dragscroll" id="gallery">


        <LeftContainer >

        <LeftContainerTopBox>
          <LeftFigure>

          <FigureImg src={Crud}
            alt="Elephant at sunset"/>
          <a href="https://zeus540.github.io/react-firebase-crud-app/" target="_blank" rel="noopener noreferrer"><FigureCaption>Firebase Crud App</FigureCaption></a>
          </LeftFigure>

          </LeftContainerTopBox>

          <LeftContainerTopBox>
          <LeftFigure>
          <FigureImg src={Port}
            alt="Elephant at sunset"/>
          <a href="https://zaheer.netlify.app" target="_blank" rel="noopener noreferrer"><FigureCaption>SPA Portfolio</FigureCaption></a>
          </LeftFigure>
          </LeftContainerTopBox>

          <LeftContainerTopBox>
          <LeftFigure>
          <FigureImg src={Kkwe}
            alt="Elephant at sunset"/>
          <a href="https://kkwe.co.za" target="_blank" rel="noopener noreferrer"><FigureCaption>Kkwe</FigureCaption></a>
          </LeftFigure>
          </LeftContainerTopBox>


          <LeftContainerTopBox>
          <LeftFigure>
          <FigureImg src={FunFox}
            alt="Elephant at sunset"/>
          <FigureCaption>Fun Fox</FigureCaption>
          </LeftFigure>
          </LeftContainerTopBox>

        </LeftContainer>

      </Container>
  );
  }
}

export default Page2;
