import React, {Component} from 'react';
import styled from 'styled-components';
import Img from '../images/clem-onojeghuo-8soQzdz9XXA-unsplash.jpg';

const Container = styled.div`
min-width: 15vw;
height: 100%;
display:flex;
position:fixed;
flex-direction:column;
z-index:20;
background:#1f1f1f;
border-right:4px floralwhite solid;
@media (min-width: 1px) and (max-width: 426px) {
 flex-direction:column
 height: unset;
}
@media (min-width: 426px) and (max-width: 1024px) {
  min-width: 30vw;
 }
`

const Logo = styled.div`
width:150px;
margin:0 auto;
`
const LogoImg = styled.img`
width:100%;
`

const Ul = styled.ul`
list-style:none;
`
const Li = styled.li`
list-style:none;
`
class Intro extends Component {

render() {

    return (

      <Container >
        <Logo>
        <LogoImg src={Img} alt=""/>
        </Logo>
 <Ul>
   <Li>HOME</Li>
   <Li>About</Li>
   <Li>Contact Us</Li>
 </Ul>

      </Container>
  );
  }
}

export default Intro;
