import React, {Component} from 'react';
import styled from 'styled-components';
import zafs from '../images/zafs.jpg'

const Container = styled.div`
  min-width: 100%;
  min-height:100%;
  position:relative;

  display:flex;
`

const LeftContainer = styled.div`
  width: 50%;
  max-height: 100vh;
  display: flex;
  flex-direction: column;

  background:#1f1f1f;
  float:left;
  @media (max-width: 480px) {
    max-height: unset;
  }
  @media (min-width: 768px) and (max-width: 1024px) and (orientation:portrait) {
    min-width: 50vw;
  }
`


const RightContainer = styled.div`
  width: 50%;
  height: 100vh;
  float:right;
  position:relative;
  background-image:url(${zafs});
  background-size:100% 100%;
  @media (max-width: 480px) {
    height:50vh;
    width: 100%;
  }
  @media (min-width: 768px) and (max-width: 1024px) and (orientation:portrait) {
    min-width: 50vw;
  }

`



const FloatingText = styled.div`
position:relative;
left:1%;
top:15%;
color:white;
z-index:20;
@media (max-width: 480px){
text-align:center;
display:none;
}
`

const Text1 = styled.h1`
font-size:120px;
margin:0px;
text-align:left;
padding:10px
`
const Text2 = styled.p`
margin:0px;
text-align:left;
background=color:black;
padding:20px
overflow-y:auto;
`

const FloatingText2 = styled.div`
position:relative;
top:20%;
color:white;
width:100%;
z-index:20;
text-align:right;
overflow-y:auto;
`

class Page1 extends Component {

render() {

    return (

      <Container color={this.props.color}>


        <LeftContainer>
        <FloatingText>
