width: 100%;
min-height: 100vh;
display:flex;
@media (min-width: 1px) and (max-width: 426px) {
 flex-direction:column
}
`

const LeftContainer = styled.div`
width: 30%;
height: 100vh;
display:flex;
flex-direction:column
@media (min-width: 426px) and (max-width: 768px) {
  width: 50%;
 }
`

const TopBox = styled.div`
width:100%;
height: 50vh;
background-image:url(${zafs});
background-size:100% 100%;
@media (min-width: 1px) and (max-width: 426px) {
  width: 100vw;
}
`

const BottomBox = styled.div`
width:100%;
height: 50vh;
background-image:url(${zafs});
background-size:100% 100%;
overflow-y: auto;
margin:20px
@media  (min-width: 1px) and (max-width: 426px) {
  width: 100vw;
}
`

const BottomBoxText = styled.p`
width:100%;
overflow-wrap: break-word;

`

const RightContainer = styled.div`
  width: 70%;
  height: 100vh;
  float:right;
  position:relative;

background-size:100% 100%;
@media  (min-width: 1px) and (max-width: 426px) {
  width: 100vw;
}
@media (min-width: 426px) and (max-width: 768px) {
  width: 100%;
 }


`

class Page1 extends Component {

render() {

    return (

      <Container color={this.props.color}>


        <LeftContainer>

          <TopBox>
          </TopBox>

          <BottomBox>
            <BottomBoxText></BottomBoxText>
          </BottomBox>

        </LeftContainer>

        <RightContainer>




        </RightContainer>

      </Container>
  );
  }
}

export default Page1;
